import logo from "./logo.svg";
import { useState } from "react";
import "./App.css";

const App = () => {
  const [todoList, setTodoList] = useState([
    { name: "Test React tool", status: "todo" },
    { name: "Write about Ant Design", status: "todo" },
    { name: "Finalize presentation", status: "todo" },
    { name: "Book flight to VN", status: "todo" },
  ]);
  const [addValue, setAddValue] = useState("");
  const onActive = (e, name) => {
    const changeList = todoList.map((task) => {
      if (task.name === name) {
        if (e.target.checked === true) {
          task.status = "Done";
        } else {
          task.status = "todo";
        }
      }
      return task;
    });
    setTodoList(changeList);
  };

  const onDelete = (name) => {
    const changeList = [];
    todoList.map((task) => {
      if (task.name !== name) {
        changeList.push(task);
      }
    });
    setTodoList(changeList);
  };

  const onAdd = () => {
    const changeList = todoList;
    changeList.push({ name: addValue, status: "todo" });
    setTodoList(changeList);
  };

  const onChange = (e) => {
    setAddValue(e.target.value);
  };
  return (
    <div>
      <link
        rel="stylesheet"
        href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"
      ></link>
      <div style={{ display: "flex", flexDirection: "row" }}>
        <input onChange={(value) => onChange(value)} />
        <button type="button" onClick={() => onAdd()}>
          Add
        </button>
      </div>
      {todoList.map((list) => (
        <div style={{ display: "flex", flexDirection: "row" }}>
          <input
            type="checkbox"
            value={list}
            onClick={(value) => onActive(value, list.name)}
          ></input>
          <div style={list.status === "Done" ? { color: "green" } : {}}>
            {list.name}
          </div>
          <button class="btn" onClick={() => onDelete(list.name)}>
            <i class="fa fa-trash"></i>
          </button>
        </div>
      ))}
    </div>
  );
};

export default App;
